import { configureStore, createSlice } from "@reduxjs/toolkit";


export const bpStore = createSlice({
    initialState:"hi",
    reducers: {
        addName: (state, name ) => `${state} ${name}`
    }
})

export const store = configureStore({
    bpStore:bpStore.reducer
});
